    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String valA = numATxt.getText();
        
        ListOperations operations = new ListOperations();
        
        Node headA = null;
        Node headB = null;
        
        headA = operations.stringToList(headA, operations.fillZeros(valA));
        
        Node product = operations.factor(headA);
        
        String res = operations.listToString(product);
        
        res = res.replaceFirst("^0+(?!$)", "");
        
        System.out.println("El factorial es " + res);

        
        
        lblResult.removeAll();
        lblResult.revalidate();
        lblResult.setText(res);
        lblResult.revalidate();
        lblResult.repaint();
    }//GEN-LAST:event_jButton1ActionPerformed

